INSERT INTO `tbl_course` (`id`, `title`, `des`, `img`, `path`, `time`, `teacher_id`, `keywords`, `list_id`, `is_first`) VALUES
(1, '嵌入式驱动开发环境搭建初级', '嵌入式系统的开发往往和PC上的开发不一样，那么本套课程将带着大家看看嵌入式开发中一些常用软件环境和服务的搭建情况。', '1.1嵌入式驱动开发环境搭建初级_btlDIHY.png', '', 0, 4, '嵌入式驱动、嵌入式开发环境、嵌入式开发环境搭建                 ', NULL, 0),
(2, 'C语言语法概述	', 'C语言，是从事嵌入式开发人员必须掌握的重要工具，本套课程将重点围绕C语言的32个关键字，掌握其中使用它们的技巧和规律。', '1.2C语言语法概述_lgZo42M.png', '', 0, 4, 'C语言、C语言语法、C语言学习、C语言教程', NULL, 0),
(3, 'C语言内存操作', '在C语言用，其核心和灵魂就是对内存类型资源的操作，这个操作的工具就是指针。本章课程嵌入式C语言高级内存空间RAM，将带领大家一起熟悉和掌握C语言指针的操作方法和思维方式。\r\n', '1.3C语言内存操作_uz5B0CW.png', '', 0, 4, 'C语言、C语言内存、C语言学习、C语言教程', NULL, 0),
(5, 'C语言函数使用', 'C语言在本质上是函数的语言，我们使用的每个功能都是函数，因此在嵌入式应用开发中，我们对C语言的应用其实是对其函数的应用。如何运用函数，将是我们设计大型嵌入式程序的基础。\r\n', '1.4C语言函数使用_OVE9HoC.png', '', 0, 4, 'C语言，C语言学习，C语言教程，C语言函数', NULL, 0),
(6, 'linux操作系统基础	', 'linux作为一款开源的操作系统，已经被大多数人所认可，在嵌入式领域中，linux的占用率一直稳居榜首，\r\n掌握好linux下的基本概念，对我们掌握系统编程及驱动开发都非常有利。', '1.5linux操作系统基础_d2l35tm.png', '', 0, 4, 'linux操作系统，linux操作系统教程，linux操作系统学习、linux系统', NULL, 0),
(7, 'Java For Android', '进一步学习Android中常用的Java知识，包括：反射、内部类、泛型、多线程、Http网络编程等知识。', '5.1Java-for-android.jpg', '', 0, 5, 'Java For Android，Java For Android视频教程，Java For Android学习', NULL, 0),
(8, '选修课 - 数据结构与算法', '数据结构课程描述：要想成为一名合格的计算机软件开发人员，必然要深入理解数据结构与算法。本套课程由美国Syracuse大学的老师带领大家掌握常用的数据结构和算法。\r\n', '5.7选修课_-_数据结构与算法.png', '', 0, 3263, '数据结构与算法、数据结构与算法学习，数据结构与算法视频教程', NULL, 0),
(9, 'Eclipse IDE 使用技巧', 'Eclipse IDE 是Java体系主流的集成开发环境，熟练掌握其使用是编程必备技能，本课程介绍Eclipse IDE开发工具的使用，快捷键，设置，技巧，代码阅读，调试等。', '1.4Eclipse-IDE-使用技巧.jpg', '', 0, 3288, 'Eclipse IDE、Eclipse IDE视频教程，Eclipse IDE学习、Eclipse IDE 使用技巧', NULL, 0),
(10, 'Android 编码规范及代码风格', '好的编程规范和代码风格对编程至关重要，本课程讲解Java&Android中的编码规范和如何编写良好风格的代码。', '1.5-Android-编码规范及代码风格.jpg', '', 0, 5, 'Android 编码规范、android代码风格、android学习、android教程', NULL, 0),
(11, 'SVN & Git 版本控制', 'SVN&Git在软件开发中经常使用，本章学习它们的使用背景、作用、用法等，以便在开发中进行版本控制，获取开源项目源代码，团队开发等。', '5.2SVN--Git-版本控制.jpg', '', 0, 5, 'SVN， Git，SVN & Git 版本控制，SVN & Git 版本控制学习', NULL, 0),
(12, 'Android App 开发环境搭建和配置', '介绍 Android App 开发环境搭建和配置操作。\r\n1、JDK&ADT (Bundle)的概念、如何获得、安装，安装目录介绍。\r\n2、配置JDK环境变量。\r\n3、Eclipse开发工具常规配置。\r\n4、Android SDK Manager&模拟器配置。', '1.1Android_App_开发环境搭建和配置.png', '', 0, 8, 'Android开发环境，android开发环境搭建，android教程，android学习', NULL, 0),
(13, 'Android App开发起手式 - HelloWorld 二三事', '通过一个简单的Android App - HelloWorld，讲解如何新建Android Application Project、Project中的目录结构和文件有哪些，有什么用，有什么规范约束等等、Android 开发相关小工具DDMS、ADB、LogCat的使用，以及大致理解一个Project中各部件如何协同工作的。', '2.1.-Android-App-开发起手式-HelloWorld二三事.jpg', '', 0, 8, 'android开发，android教程，android入门教程，android学习', NULL, 0),
(15, 'Android 基础UI控件', 'Android的UI控件是开发App应用程序界面设计的主要组件，一个商业级界面的制作，需要对android UI控件的熟练掌握和灵活使用。本章讲解Android界面开发中UI控件使用。', '2.3.-Android-的基础UI控UIkong.jpg', '', 0, 5, 'Android UI控件，android学习，android ui教程', NULL, 0),
(16, '秒学Swift开发语言，坐标系计算机语言学习法', 'Swift开发语言，无疑是目前最火的编程语言。不过你想知道如何秒学Swift语言么？你想知道如何深入理解这门语言么？你想知道如何不忘记这门语言么？你想知道如何秒学其他计算机编程语言么？本课程讲师Sundy带你进入一个学习计算机语言方法的全新世界 ，深入的掌握Swift语言 ，并且了解Swift的前世今生 ，深入浅出，引导你的思考和学习方法，一堂课的学习，终生的收益 ，这就是我们的课程风格。国内最与众不同的Swift课程，这套课程学完，其余语言也都通吃了。', '1.1秒学Swift开发语言坐标系计算机语言学.png', '', 0, 5, 'swift语言，swift学习，swift教程，iOS开发', NULL, 0),
(17, 'iOS8 App开发快速入门', '此为iOS应用开发职业课程系列的其中一套课程。让已经有swift语言基础的朋友快速掌握iOS开发的整个流程，并且做出自己的第一个Demo小例子 。麻雀虽小五脏俱全 。这个课程学完iOS开发流程和技术你就掌握了，后面无非就是再学习更多的Kit方法调用 。\r\n这套课程也是国内第一套基于swift语言的ios8应用开发课程 。 现在就开始 ， 快速掌握iOS8的基本开发吧 。', '1.3iOS8_App开发快速入门.png', '', 0, 5, 'iOS开发，iOS入门，iOS教程，iOS学习', NULL, 0),
(18, 'iOS开发玩转界面-UIKit', 'UIKit框架是iPhone应用程序开发中最基本的框架，也是用得最多、最重要的框架，从名字我们就能理解是界面相关操作组件集合。本套课程系统全面的学习iOS界面组件UIKit，不但熟悉各个界面组件操作，更重要的是深入浅出的理解原理，实在是市面不可多得的iOS界面教学课程。', '2.1iOS开发玩转界面-UIKit.png', '', 0, 5, 'iOS界面，iOS开发，UIKit教程，UIKit学习', NULL, 0),
(21, 'Linux文件IO操作	', '本章嵌入式应用开发常用技术Linux文件IO操作，将由麦子学院资深嵌入式应用开发工程师带你深入学习掌握文件的IO操作，让你体会Linux“一切皆文件”的设计特。', '2.1Linux文件IO操作_DKNyhBj.png', '', 0, 5, 'Linux系统，Linux学习，Linux文件IO操作', NULL, 0),
(22, 'Linux应用开发之进程间通信	', '进程，程序灵魂的载体，掌握了linux的进程及特性，以及他们之间的信息传递，将是成为一名合格的linux应用开发工程师必须的技能。', '2.2Linux进程间通信_90qJDLw.png', '', 0, 5, 'Linux系统，Linux进程，Linux学习', NULL, 0),
(23, 'iOS多线程及异步任务处理', '学习iOS多线程开发和异步任务处理 ， 会学习到NSThread ， TaskQueue，Runloop，GCD等 ， 也会学习在商业项目中哪些功能我们需要用多线程和异步任务，从而达到优秀的质量 。 ', '3.2iOS多线程及异步任务处理.png', '', 0, 2212, 'iOS多线程，iOS异步任务处理，iOS学习，iOS教程', NULL, 0),
(25, '普及课 - ARM开发板基础知识', '嵌入式驱动开发学习中，对于硬件零基础，我们如何认识一款ARM开发板呢？让我们跟着视频一点一点熟悉什么是ARM开发板。', '3.1选修_-_ARM开发板基础知识.png', '', 0, 5, 'ARM开发板，ARM开发板学习，ARM开发板教程', NULL, 0),
(26, 'iOS网络编程', '学习如何在iOS开发中用到网络通信 ，包括BSD套接字，CFNetwork framework，Foundation framework API，CFURL 等 ，我们会重点以HTTP协议来熟悉iOS网络编程相关知识', '3.1iOS网络编程.png', '', 0, 6502, 'iOS网络编程，iOS学习，iOS教程', NULL, 0),
(27, 'ARM汇编语言', '学习ARM，首先要了解ARM的工作原理，那么对ARM汇编的理解，将是我们理解ARM体系结构最好的敲门砖。', '3.2选修_-_ARM汇编语言.png', '', 0, 5, 'ARM汇编语言，ARM汇编语言学习，ARM教程', NULL, 0),
(28, 'ARM硬件开发基础', '独木不成林，嵌入式驱动开发学习中，只是理解了ARM的结构，系统还是无法使用，下面我们将了解芯片中的一些简单控制器，来看看系统是如何展现给我们的。', '3.3选修_-_ARM硬件开发基础.png', '', 0, 5, 'ARM硬件，ARM硬件开发，ARM硬件开发教程', NULL, 0),
(29, 'ARM Bootloader开发', '对于嵌入式驱动开发，掌握了简单的硬件操作，但还不够，一旦系统变得复杂多变时，我们只靠一些简单的汇编是无法开发大型系统的，那么本课程将带着大家掌握如何一步一步写出ARM下的bootloader子功能。', '3.4ARM_Bootloader开发_f6TPFQr.png', '', 0, 4, 'ARM Bootloader开发，ARM教程，ARM学习', NULL, 0),
(30, 'ARM硬件接口开发', '嵌入式驱动开发中，有了bootloader，我们可以放心的开发更复杂的接口了，helloworld是怎么在底层跑起来那，看起来不像printf那样简单...', '3.5ARM硬件接口开发_uUa4yrX.png', '', 0, 4, 'ARM硬件接口，ARM硬件接口开发，ARM硬件接口教程', NULL, 0),
(31, 'iOS多媒体编程', '学习iOS的音频视频编程的各个方面 ，包括上层的类似于MPMoviePlayerViewController类的学习 ，以及底层的CoreAudio等学习等等 。课程学完后能独立开发出高质量的音频和视频程序 。', '3.3iOS多媒体编程.png', '', 0, 8782, 'iOS多媒体，iOS多媒体编程，iOS开发，iOS教程', NULL, 0),
(32, '探寻嵌入式系统移植的世界', ' 嵌入式底层驱动学习的前提，是要将我们的开发板上运行起linux操作系统，那么本课程将由麦子学院资深嵌入式开发工程师王海宁老师带领大家学习如何移植系统三大组件的步骤，bootloader，linux内核，文件系统的过程。', '4.1探寻嵌入式系统移植的世界_PtsUqDf.png', '', 0, 4, '嵌入式，嵌入式系统，嵌入式系统移植，嵌入式教程', NULL, 0),
(33, 'iOS地理位置及定位系统', '学习包括MapKit等与定位及地图相关的应用开发', '3.4iOS地理位置及定位系统.png', '', 0, 8783, 'iOS地理位置，iOS定位系统，iOS教程', NULL, 0),
(34, '看懂uboot的神秘面容', '这套“看懂uboot的神秘面容”课程，其目的在于通过对一个优秀的bootloader软件的全过程分析，一步步如抽丝剥茧般带你领略嵌入式开发所涉及的系统启动部分涉及的概念和相关硬件的原理。包括CP15、UART、DDR2这些系统启动必须掌握的设备信息。', '4.2看懂uboot的神秘面容_W0LtJiA.png', '', 0, 2027, 'uboot，uboot学习，uboot教程，uboot视频教程', NULL, 0),
(38, 'linux字符设备驱动初探', 'Linux字符设备驱动程序是Linux下被组织为一组完成不同任务的函数的集合，通过这些函数使得linux的设备操作犹如文件一般。本章就由麦子学院嵌入式开发名师带领大家一起学习linux字符设备驱动相关知识，赶快开始学习吧。\r\n', '4.4linux字符设备驱动初探_kpaklhQ.png', '', 0, 6693, 'linux字符设备，linux字符设备驱动，linux教程', NULL, 0),
(42, 'linux下输入子系统', '在嵌入式开发中，Linux输入子系统是由输入子系统设备驱动层、输入子系统核心层(Input Core)和输入子系统事件处理层(Event Handler)组成。本章我将一起学习input子系统的相关知识，了解Linux输入子系统的核心原理。\r\n', '4.6linux下输入子系统_IEgCGZf.png', '', 0, 6693, 'linux系统，linux下输入子系统，linux教程', NULL, 0),
(45, 'linux下网卡设备驱动', '学习嵌入式驱动开发，本章将由麦子学院嵌入式资深讲师，带你一起学习linux下设备驱动相关知识，掌握DM9000驱动相关知识。\r\n', '5.3linux下网卡设备驱动_lTj2KpF.png', '', 0, 6693, 'linux系统，linux下网卡设备驱动，linux教程', NULL, 0),
(54, '选修-敏捷开发软件工程', '学习软件开发流程，掌握使用UML，并且以WRUP软件工程模型为基础，进行软件开发规范化和团队管理的实操！', '5.3选修-敏捷开发软件工程.png', '', 0, 5, 'iOS开发，敏捷开发，敏捷开发教程', NULL, 0),
(55, 'Android 项目实战一：AA小助手', '在学习完成Android的Activity和一些常用android UI控件后，我们以一个小android项目实战来练习我们学习的内容，目的在于：锻炼布局、熟练掌握常用控件的使用，控件的数据绑定，Activity的生命周期，Activity跳转，Activity间传递数据。', '2.4.-实战项目一.jpg', '', 0, 3288, 'Android实战，Android教程，AA小助手', NULL, 0),
(56, 'Android Service & Broadcast', 'Service和Broadcast是Android的四大件之一，在Android开发中经常使用。本课程将讲解Service和Broadcast的应用场景，使用规则，如何在App开发中使用他们。', '3.1.-Android-Service--Broadcast.jpg', '', 0, 5, 'Android Service，Android Broadcast，Android教程', NULL, 0),
(59, 'Android 多线程与异步任务1', '本课程将学习Android编程中的多线程和异步任务相关操作，帮助学员理解、掌握android多线程、android异步任务相关理论知识的同时，也能将这些理论运用于具体实践。', '3.4..-Android-多线程及异步任务.jpg', '', 0, 8, 'Android 多线程，Android异步任务，安卓教程，安卓学习', NULL, 0),
(61, 'Android 应用部署与发布', 'android应用发布程序让你的劳动成果公布于世，打包和发布是这个神圣动作的必备步骤。本课程介绍了android应用部署与发布中的打包程序时的混淆技术，反编译和防止反编译技术。手动打包和Ant脚本打包的介绍，详细说明版本名称和版本代号的作用，说明签名文件的作用。介绍批量打包的版本，将android程序发布到各大安卓市场上。', '4.1.-Android-应用部署与发布.jpg', '', 0, 3270, 'Android 应用部署，安卓应用发布，安卓教程', NULL, 0),
(62, 'Android 主题及动画的实现', 'android的主题、自定义样式以及动画的实现', '3.6Android_主题及动画的实现.png', '', 0, 8, 'Android 主题实现，Android动画实现，Android教程，Android学习', NULL, 0),
(64, 'Android Camera & Recoder', 'Android Camera拍照|Recoder录像功能的开发要点和技巧。 ', '5.3.-Android-Camera--Recorder.jpg', '', 0, 3262, 'Android Camera，Android Recoder，Android教程，Android学习', NULL, 0),
(65, 'Android 多媒体', '学习Android的音频和视频播放。', '5.4.-Android-多媒体.jpg', '', 0, 5, '安卓多媒体，Android教程，Android学习', NULL, 0),
(66, 'Android 传感器 Sensor', 'Android中各种传感器:重力、加速度、方向、光线的使用方法。', '5.5.-Android-传感器-Sensor.jpg', '', 0, 3262, 'Android 传感器，安卓教程，安卓学习', NULL, 0),
(67, 'Android 社会化分享', '学习Android的中社会化分享技术的实现，包括：各大平台官方实现、第三方框架实现，社会化分享数据统计。', '5.7.-Android-社会化分享.jpg', '', 0, 3261, 'Android 社会化分享，安卓教程，安卓学习', NULL, 0),
(68, 'Android 消息推送', '随着移动互联网的不断发展，Android开发成为了大多数程序员选择的对象，但是非常多的人都是半路出家，根本就不明白Android开发其中的一些原理以及实现的方法，只是能够片面性的运用别人写好的代码。\r\n因此：本套视频教程将从实践的角度带你学习Android推送的知识。在使用中理解Android推送的原理。通过对Android推送基本原理的了解，进一步的熟悉三方平台推送库，从更多的方面理解和学习Android的推送。\r\n', '5.8.-Android-消息推送.jpg', '', 0, 15571, 'Android 消息推送，安卓教程，安卓学习', NULL, 0),
(69, 'Android 定位技术 LBS', '本课程学习Android中LBS定位技术的实现。', '5.9.-Android-定位系统-LBS.jpg', '', 0, 4859, 'Android 定位技术，LBS，安卓教程，安卓学习', NULL, 0),
(70, 'Android NDK 初步', '本课程学习Android的NDK和JNI的技术使用。', '5.10.-Android-NDK初步.jpg', '', 0, 5, 'Android NDK，Android NDK入门，安卓教程，安卓学习', NULL, 0),
(71, 'Android 电话与短信应用', '本课程学习Android中电话和短信的使用。', '5.11.-Android-电话与短信应用.jpg', '', 0, 5, 'Android应用，电话与短信，安卓教程，安卓学习', NULL, 0),
(72, 'Android Launcher & Widget', '本课程学习Android中Launcher和Widget的使用。', '5.12.-Android-Launcher--Widget.jpg', '', 0, 5, 'Android Launcher，Android Widget，安卓教程，安卓学习', NULL, 0),
(82, '选修 - ARM开发板基础知识', 'ARM开发板，嵌入式开发常用工具之一，以英国ARM（Advanced RISC Machines）公司的内核芯片作为CPU，同时附加其他外围功能的嵌入式开发板，用以评估内核芯片的功能和研发各科技类企业的产品。本节将由麦子学院高级嵌入式开发工程师带你进入ARM开发板的世界，让你从一点一滴开始熟悉ARM开发版。\r\n\r\n', '3.1选修_-_ARM开发板基础知识_BoA6mYW.png', '', 0, 4, 'ARM开发板，ARM开发板教程，嵌入式', NULL, 0),
(83, '选修 - ARM汇编语言', '独木不成林，只是理解了ARM的结构，系统还是无法使用，本章我们将学习ARM汇编语言，了解芯片中的一些简单控制器，来看看系统是如何展现给我们的。', '3.2选修_-_ARM汇编语言_dc28YKd.png', '', 0, 4, 'ARM汇编语言，ARM教程，嵌入式', NULL, 0),
(84, '选修 - ARM硬件开发基础', '嵌入式开发，软件与硬件的结合，了解完ARM的结构后，我们还需要对ARM硬件的开发进行一些了解，本节将由麦子学院资深嵌入式开发工程师，带你了解芯片中的一些简单控制器，玩转嵌入式ARM开发。\r\n', '3.3选修_-_ARM硬件开发基础_NNABRNo.png', '', 0, 4, 'ARM硬件开发，ARM教程，嵌入式', NULL, 0),
(92, '嵌入式C语言高级---语法概述', 'c语言，物联网开发基础语言，是从事物联网开发人员必须掌握的重要工具，本章c语言语法概述将由麦子学院资深物联网开发工程师王海宁老师为大家讲解C语言所包含的32个关键字，及其在物联网中的应用技巧与规律。\r\n', '1.1嵌入式C语言高级---语法概述.png', '', 0, 4, '嵌入式，C语言，C语言语法，C语言教程', NULL, 0),
(93, '嵌入式C语言高级---内存空间', '在物联网开发中，C语言的核心和灵魂就是对内存资源的操作，而在开发中，这个操作的工具就是指针，本章麦子学院物联网资深工程师将带领大家一起来学习掌握C语言及其指针工具的使用。\r\n\r\n', '1.2嵌入式C语言高级---内存空间.png', '', 0, 4, '嵌入式，C语言，C语言内存，C语言教程', NULL, 0),
(94, '嵌入式C语言高级---函数空间', '物联网开发中所运用的C语言其本质是一个函数语言，其编写的每个功能都是一个函数。在物联网开发中，如何运用C语言的函数，是设计大型物联网程序的必要基础。\r\n', '1.3嵌入式C语言高级---函数空间.png', '', 0, 4, '嵌入式，C语言，C语言函数，C语言教程', NULL, 0),
(98, '物联网之ucos操作系统基础', '物联网之ucos操作系统基础', 'ucos操作系统基础.jpg', '', 0, 33547, '物联网，物联网教程，ucos操作系统', NULL, 0),
(103, '互联网发展和基础', '工欲善其事必先利其器，从事产品经理的工作怎么不先了解互联网的发展历程呢？本部分课程带领大家从最容易记忆的概念和指标入手，伴以常见产品发展案例，将产品及产品经理相关概念做以介绍。', 'fanzhanjichu.png', '', 0, 46, '互联网，产品经理，产品经理教程', NULL, 0),
(104, '互联网产品及产品经理', '学习从多个维度分析和研究产品，进而从多个角度来思考如何打造自己的产品。产品经理是什么样的角色，做出来的产品如何评价和判断优劣？产品经理的发展轨迹是什么样的？本部分将做以介绍，做到有的放矢。', '1.2part2-互联网产品及产品经理.jpg', '', 0, 46, '互联网产品，产品经理，产品经理教程', NULL, 0),
(105, '互联网产品的商业模式', '商业模式是产品经理构建产品的基石，互联网产品的的各种商业模式都有哪些，了解了这些商业模式，做产品或者研究产品才能透过现象看本质。', 'shangyemoshi.png', '', 0, 46, '互联网产品，商业模式，产品经理，产品经理教程', NULL, 0),
(106, '产品的商业模式画布', '学习产品商业模式画布中的元素，能够更好地看待产品、资源、目标，学习如何结构化的分析整理自己的资源，构建整个产品的大的结构。', '1.4part4-产品的商业模式画布.jpg', '', 0, 46, '商业模式画布，产品经理，产品经理教程', NULL, 0),
(107, '互联网思维', '互联网产品发展日新月异，在各种形态的产品背后，有哪些思维方式在发挥作用，从一定高度让大家学习如果将抽象思维转化为产品实体，把握关键脉络和互联网思维方式，顺应趋势打造产品。', 'siwei.png', '', 0, 46, '互联网思维，产品经理，产品经理教程', NULL, 0),
(108, '流程图', '产品经理梳理过程的利器——流程图，如何绘制流程图，如何组织要绘制的节点内容，用哪些工具可以更有效地绘制流程图，本部分产品流程图课程将深入浅出的带领大家实际训练起来。', 'liuchengtu.png', '', 0, 46, '产品经理，流程图，产品经理教程', NULL, 0),
(109, '需求分析及BRD', '产品经理日常工作会产出的需求和想法，这些需求和想法如何管理，如何传递，如何组织，本部分课程带领大家学习需求的管理和BRD的撰写，开始完成第一个重要的文档。', '2.2part7-需求分析及BRD.jpg', '', 0, 46, '产品经理，需求分析，BRD，产品经理教程', NULL, 0),
(110, '市场需求文档MRD', '本部分课程将学习重要的规划型的产品市场需求文档，其中如何分析市场、如何分析用户、如何构建产品目标、如何进行项目规划，都将进行深入浅出的学习。', '2.3part8-市场需求文档MRD.jpg', '', 0, 46, '产品经理，市场需求文档，MRD，产品经理教程', NULL, 0),
(111, '产品功能需求文档PRD', '产品经理日常工作中，具体的产品功能需求文档的撰写如何进行，包括哪些部分内容，如何描述功能，本课程将以实际的案例进行讲解如何完成产品PRD。', '2.4part9-产品功能需求文档PRD.jpg', '', 0, 46, '功能需求文档，PRD，产品经理，产品经理教程', NULL, 0),
(112, '竞品分析', '关注竞品进行分析是产品经理日常工作的重要部分，本部分课程将完整的介绍竞品分析如何组织，有哪些方法，以什么样的形式呈现，配合实际案例做全面的介绍。', '2.5part10-竞品分析.jpg', '', 0, 46, '竞品分析，产品经理，产品经理教程', NULL, 0),
(113, '数据分析', '产品数据分析是产品经理工作的重要组成部分，依据数据变化来调整产品是有效的工作方式，如何进行产品数据分析，分析哪些数据，怎么转化数据未行动，本部分课程都将进行介绍。', '3.1part11-数据分析.jpg', '', 0, 46, '产品经理，数据分析，产品经理教程', NULL, 0),
(114, '产品运营', '产品不仅需要设计还需要运营，产品运营是在做什么，有哪些方式，对产品能起到什么作用，如何做产品运营，本部分课程将带领大家进行学习。', 'chanpin.png', '', 0, 46, '运营，产品经理，产品经理教程', NULL, 0),
(115, 'Web端产品设计', '产品是用来满足用户的，以用户为中心的产品设计如何进行，在整个产品设计过程中有哪些关键点，交互设计、视觉设计如何发挥作用，本部分课程将深入学习。', '3.3part13-产品设计.jpg', '', 0, 46, '产品设计，产品经理，产品经理教程', NULL, 0),
(116, '移动端产品设计', '移动互联网产品发展越发火爆，这些产品设计上有什么不同，移动产品的体验有什么特征特点，有哪些原则可以更好的打造移动产品，本部分课程将全面解读。', '3.4part14-移动产品设计.jpg', '', 0, 46, '移动端，产品设计，产品经理，产品经理教程', NULL, 0),
(229, 'Javascript基础入门', 'Javascript是web前端开发常用技术之一，也是web前端开发必学技术之一，本章麦子学院原创JavaScript视频教程，将围绕其相关基础知识和实际应用全面讲解，让你轻松掌握并精通JavaScript的使用。', 'JavaScript_jichurumen.png', '', 0, 4290, 'Javascript，Javascript教程，Javascript学习，web前端开发', NULL, 0),
(230, 'Web前端开发之Ajax初步', '麦子学院原创web前端开发常用技术 — ajax视频教程， 主要从ajax的运行环境、元素的应用、及其数据类型的运用等方面进行介绍和实战，让学员能快速入门并精通ajax的使用。\r\n', '1.4Web前端开发之Ajax初步_dzETH8z.jpg', '', 0, 1161, 'Ajax入门，Ajax入门教程，Ajax学习，web前端开发', NULL, 0),
(231, 'Photoshop切片', 'Photoshop切片是web前端开发常用技术之一，本章将由麦子学院资深web前端工程师为你讲解ps切片的基本操作、技巧及要点，让你快速入门Photoshop切片。\r\n', '1.5Web前端开发之Photoshop切片.jpg', '', 0, 1470, 'Photoshop切片，Photoshop教程，Photoshop学习，web前端开发', NULL, 0),
(232, 'Web前端开发之jQuery入门课程', 'JQuery — web前端开发的另一常用技术，本章将从理论基础+实战演练两方面对JQuery相关应用进行全面讲解，让你轻松学习JQuery 。', '1.6Web前端开发之jQuery入门课程.jpg', '', 0, 1393, 'jQuery入门，jQuery入门教程，jQuery学习，web前端开发', NULL, 0),
(233, '前端开发项目：一起来做秒杀活动', '通过使用HTML5+CSS+JS技术进行秒杀活动前端开发项目实战演练达到熟练掌握的程度。', '2.1前端开发项目--一起来做秒杀活动.jpg', '', 0, 1161, 'web前端，web前端开发，web前端教程，web前端项目实战', NULL, 0),
(234, '选修-XCode详解及iOSApp上传', '学习如何安装和使用XCode以及iOS的App应该如何上传到app store上。', '5.4选修-XCode详解及iOSApp上传.jpg', '', 0, 1484, 'XCode详解，iOSApp上传，iOS开发，iOS教程', NULL, 0),
(235, '选修-Objective-c之iOS开发入门实战', '大富翁作者，硅谷柯博文老师给大家带来的objective-c语言与IOS开发实战入门课程，全面讲解iOS开发环境搭建、iOS开发工具的使用及相关函数、类的用法。', '5.5选修-Objective-C的重点难点分析-拷贝.jpg', '', 0, 1481, 'Objective-c，iOS实战，iOS开发，iOS教程', NULL, 0),
(236, '选修-Objective-C的重点难点分析', '针对objective-c编程语言入门之后的重点难点知识讲解， 包括Objective-C内存管理 、 Block 、Objective-C 可变序列、Objective-C可变字典等等，轻松掌握Objective-C开发语言重难点知识。', '5.2选修-Objective-C的重点难点分析.jpg', '', 0, 1484, 'Objective-C语言，Objective-C教程，iOS教程，Objective-C学习', NULL, 0),
(256, 'android架构师需要掌握的OOP及架构分析', '本章主要由麦子学院Android资深名师高焕堂老师，为大家讲解android架构师需要掌握的OOP知识及其架构分析，为想成为Android架构师的你奠定基础。', '1.1android架构师需要掌握的OOP及架构分析.png', '', 0, 2021, 'android架构，OOP，架构分析，android教程，android学习', NULL, 0),
(257, 'android架构之EIT介绍', 'EIT造形是一种基本的结构(Structure)，一种概念(Concept)，是Android架构师常用技术之一，本章高焕堂老师将为你详细讲解EIT造形相关应用，让我们一起探索单一造形所创造出来的整体之美。', '1.2android架构之EIT介绍.png', '', 0, 2021, 'android架构，EIT，android教程，android学习', NULL, 0),
(258, 'android架构之设计模式介绍', '麦子学院原创android架构之设计模式课程，全面讲解Android架构设计中的常用模式，与初入门架构设计的你一起探索Android领域。', '1.3android架构之设计模式介绍.png', '', 0, 2021, 'android架构，设计模式，android教程，android学习', NULL, 0),
(259, 'android架构之认识框架', '要想成为一名android架构师，认识android框架非常有必要，通过本章学习，你将可全面掌握android 的java应用层框架。\r\n', '2.1android架构之认识框架.png', '', 0, 2021, 'android架构，认识框架，android教程，android学习', NULL, 0),
(260, 'android架构之认识进程与IPC架构', 'android中的IPC的基本模型是基于客户/服务器(C/S)架构的，学习本章麦子学院原创IPC架构课程，将使你对IPC架构有更全新的认识，同时对相关知识的掌握度也更加深入。', '2.2android架构之认识进程与IPC架构.png', '', 0, 2021, 'android架构，进程，IPC架构，android教程，android学习', NULL, 0),
(261, 'android架构之认识线程模式', '线程是android架构中常用的重要模式之一，也是android架构师必须掌握的技术之一，本章就由麦子学院的高焕堂老师带大家一起来认识认识什么是android架构的线程模式。', '2.3android架构之认识线程模式.png', '', 0, 2021, 'android架构，线程模式，android教程，android学习', NULL, 0),
(262, 'android架构之UI框架及多线程', 'android架构设计中，架构很重要，UI也同样重要，通过学习本章UI框架及多线程相关知识，将助你设计出更完美的android界面。\r\n', '2.4android架构之UI框架及多线程.png', '', 0, 2021, 'android架构，UI框架，多线程，android教程，android学习', NULL, 0),
(263, 'android架构之AIDL与Proxy-Stub设计模式', 'AIDL与Proxy-Stub设计模式是android架构的另一个重要技术，学习此课程，将使你更全面的掌握android架构设计所需相关技能技巧。', '2.5android架构之AIDL与Proxy-Stub设计模.png', '', 0, 2021, 'android架构，AIDL，Proxy-Stub设计模式，android教程，android学习', NULL, 0),
(264, 'android架构之IBinder与IMessager', '本章android架构设计的IBinder与IMessager接口，麦子学院遵循敏捷的迭代过程，从思想、方法、模式、范例和成功案例各种不同角度，带你学习从设计到代码的途径，让你迅速熟悉并能熟练使用这两个接口。\r\n', '2.6android架构之IBinder与IMessager.png', '', 0, 2021, 'android架构，IBinder，IMessager，android教程，android学习', NULL, 0),
(265, 'android架构之JNI详解', '上层Java要调用底层的C/C++函数库必须通过Java的JNI来实现，本章我们将一起学习Android架构JNI的原理、接口、函数、设计等等，一起探索Android是如何通过JNI来实现Java对C/C++函数的调用的。', '2.7android架构之JNI详解_gLi8KF4.png', '', 0, 2021, 'android架构，JNI，android教程，android学习', NULL, 0),
(266, 'android架构之Java线程篇', '本章android架构设计得java线程，将从多个java纯种进入本地函数和本地线程进入java层两个方面进行详细的理论+实践学习，全面掌握java线程的运用。\r\n', '2.8android架构之Java线程篇.png', '', 0, 2021, 'android架构，Java线程，android教程，android学习', NULL, 0),
(267, 'android架构之复习问题集', 'Android架构历练的第二个阶段，框架原理及架构学习已告一段落，对于学习中存在的一些问题，本章将统一讲解，我们一起来看看吧。\r\n', '2.9android架构之复习问题集.png', '', 0, 2021, 'android架构，android架构设计，android教程，android学习', NULL, 0),
(268, 'android架构之核心服务', '麦子学院原创android架构核心服务课程，由android名师高焕堂老师主讲，带你打入android架构的核心区域。', '3.1android架构之核心服务.png', '', 0, 2021, 'android架构，android架构设计，android教程，android学习', NULL, 0),
(269, 'android架构之Proxy-Stub设计模式', 'Android架构开发的Proxy-Stub设计模式课程，将从Proxy-Stub设计模式的基本概念、Proxy-Stub设计模式实战练习、及其与其他Android架构重要接口的联系几个方面进行讲解，全让你轻松掌握框架通用性接口的设计。', '3.2android架构之Proxy-Stub设计模式.png', '', 0, 2021, 'android架构，Proxy-Stub设计模式，android教程，android学习', NULL, 0),
(270, 'android架构之HAL', 'Android的HAL是为了一些硬件提供商提出的“保护proprietary”的驱动程序而产生的，简而言之，就是为了避开linux kernel的GPL license的束缚，因此在Android架构的学习中，HAL学习也是非常有必要的，我们一起来学习吧。', '3.3android架构之HAL.png', '', 0, 2021, 'android架构，HAL，android教程，android学习', NULL, 0),
(271, 'android架构之观摩Session模式与Proxy-Stub模式的搭配', 'android架构设计中，借鉴其他好的技术是必要的，本章就带领大家一起观摩Session模式与Proxy-Stub模式的搭配。', '3.4android架构之观摩Session模式与Proxy-.png', '', 0, 2021, 'android架构，Session模式，Proxy-Stub模式，android教程，android学习', NULL, 0),
(272, 'android架构之几个组件框架的设计', 'android架构的设计离不开框架的设计，本章高焕堂老师将带领大家一起学习android架构的几个常用小框架，并和大家一起动手进行框架设计。\r\n', '3.5android架构之几个组件框架的设计.png', '', 0, 2021, 'android架构，组件框架，android教程，android学习', NULL, 0),
